// import {  FILTER__COLOR_FILTER_CHANGED, TODOS__ALL_COMPLETED, TODOS__COLOR_SELECTED, TODOS__COMPLETED_CLEARED, TODOS__TODOS_LOADED, TODOS__TODOS_LOADING, TODOS__TODO_ADDED, TODOS__TODO_DELETED, TODOS__TODO_TOGGLED } from "../../actionTypes"

import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";


export const fecthTodos = createAsyncThunk('todos/fetchTodos', async ()=> {
    const response = await fetch('https://jsonplaceholder.typicode.com/todos?id=1&id=2&id=3&id=4&id=5')
    const data = await response.json()
    const todos = data.map(( todo ) => Object.assign({"id": todo.id, "text": todo.title, "color": "", "completed": todo.completed }) )
    return todos
})
