const initialState = {
  status: 'idle',
  entities: {}
};
export const fetchTodos = createAsyncThunk('todos/fetchTodos', async () => {
  const response = await fetch('https://jsonplaceholder.typicode.com/todos?id=1&id=2&id=3&id=4&id=5');
  const data = await response.json();
  const todos = data.map(todo => Object.assign({
    "id": todo.id,
    "text": todo.title,
    "color": "",
    "completed": todo.completed
  }));
  return todos;
});
export const saveNewTodos = createAsyncThunk('todos/saveNewTodo', async text => {
  const initialTodo = {
    text
  };
  const response = await fetch('https://jsonplaceholder.typicode.com/todos', {
    method: 'POST',
    body: JSON.stringify(initialTodo),
    headers: {
      'Content-type': 'application/json; charset=UTF-8'
    }
  });
  const newTodo = await response.json();
  return newTodo;
});
export const updateTodo = createAsyncThunk('todos/updateTodo', async _ref => {
  let {
    text,
    todoId
  } = _ref;
  const initialTodo = {
    text
  };
  const response = await fetch(`https://jsonplaceholder.typicode.com/todos?id=${todoId}`, {
    method: 'PUT',
    body: JSON.stringify(initialTodo),
    headers: {
      'Content-type': 'application/json; charset=UTF-8'
    }
  });
  const updatedTodo = await response.json();
  return updatedTodo;
});
export const deleteTodo = createAsyncThunk('todos/deleteTodo', async todoId => {
  const response = await fetch(`https://jsonplaceholder.typicode.com/todos/${todoId}`, {
    method: 'DELETE',
    headers: {
      'Content-type': 'application/json; charset=UTF-8'
    }
  });
  const deletedTodo = await response.json();
  return {
    id: todoId,
    deletedTodo
  };
});
const todosSlice = createSlice({
  name: 'todos',
  initialState,
  reducers: {
    todoAdded(state, action) {
      const todo = action.payload;
      state.entities[todo.id] = todo;
    },

    todoToggled(state, action) {
      const todoId = action.payload;
      const todo = state.entities[todoId];
      todo.completed = !todo.completed;
    },

    todoColorSelected: {
      reducer(state, action) {
        const {
          color,
          todoId
        } = action.payload;
        state.entities[todoId].color = color;
      },

      prepare(todoId, color) {
        return {
          payload: {
            todoId,
            color
          }
        };
      }

    },

    todoDeleted(state, action) {
      delete state.entities[action.payload];
    },

    allTodosCompleted(state, action) {
      Object.values(state.entities).forEach(todo => {
        todo.completed = true;
      });
    },

    completedTodosCleared(state, action) {
      Object.values(state.entities).forEach(todo => {
        if (todo.completed) {
          delete state.entities[todo.id];
        }
      });
    }

  },
  extraReducers: builder => {
    builder.addCase(fetchTodos.pending, (state, action) => {
      state.status = 'loading';
    }).addCase(fetchTodos.fulfilled, (state, action) => {
      const newEntities = {};
      action.payload.forEach(todo => {
        newEntities[todo.id] = todo;
      });
      state.entities = newEntities;
      state.status = 'idle';
    }).addCase(saveNewTodos.fulfilled, (state, action) => {
      const todo = action.payload;
      state.entities[todo.id] = todo;
    }).addCase(updateTodo.pending, (state, action) => {
      const todoId = action.payload.id;
      const text = action.payload.text;
      state.entities[todoId] = text;
    });
  }
});
export const {
  todoAdded,
  todoToggled,
  todosLoading,
  todoColorSelected,
  todoDeleted
} = todosSlice.actions;
export default todosSlice.reducer;

